import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { GeneralContext } from '../context/GeneralContext';
import { Calendar, MapPin, Plane, RotateCcw, Search } from 'lucide-react';
import { Card, CardContent } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  
  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);
  
  const cities = [
    "Chennai", "Bangalore", "Hyderabad", "Mumbai", "Indore",
    "Delhi", "Pune", "Trivandrum", "Bhopal", "Kolkata",
    "Varanasi", "Jaipur"
  ];

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') {
      navigate('/admin');
    } else if (userType === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, [navigate]);

  const fetchFlights = async () => {
    try {
      setLoading(true);
      if (checkBox) {
        if (!departure || !destination || !departureDate || !returnDate) {
          setError("Please fill all the inputs");
          return;
        }

        const date = new Date();
        const date1 = new Date(departureDate);
        const date2 = new Date(returnDate);

        if (!(date1 > date && date2 > date1)) {
          setError("Please check the dates");
          return;
        }
      } else {
        if (!departure || !destination || !departureDate) {
          setError("Please fill all the inputs");
          return;
        }

        const date = new Date();
        const date1 = new Date(departureDate);

        if (!(date1 >= date)) {
          setError("Please check the dates");
          return;
        }
      }

      setError("");
      const response = await fetch('http://localhost:6001/fetch-flights');
      const data = await response.json();
      setFlights(data);
    } catch (err) {
      setError("Failed to fetch flights. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleTicketBooking = (id, origin, destination) => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      navigate('/auth');
      return;
    }

    if (origin === departure) {
      setTicketBookingDate(departureDate);
    } else if (destination === departure) {
      setTicketBookingDate(returnDate);
    }
    navigate(`/book-flight/${id}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-white">
      {/* Hero Section */}
      <div className="relative h-[500px] bg-blue-600 overflow-hidden">
        <div className="absolute inset-0">
          <img 
            src="/api/placeholder/1920/500" 
            alt="Aircraft in sky" 
            className="w-full h-full object-cover opacity-20"
          />
        </div>
        <div className="relative container mx-auto px-4 py-20 text-white">
          <h1 className="text-5xl font-bold mb-6">
            Embark on an Extraordinary Flight Journey
          </h1>
          <p className="text-xl max-w-2xl">
            Discover seamless travel experiences with our premium flight booking service.
            Your journey to unforgettable destinations begins here.
          </p>
        </div>
      </div>

      {/* Search Form */}
      <div className="container mx-auto px-4 -mt-20 mb-12">
        <Card className="p-6 shadow-xl">
          <CardContent>
            <div className="flex items-center gap-4 mb-6">
              <div className="flex items-center gap-2">
                <input
                  type="checkbox"
                  id="returnTrip"
                  checked={checkBox}
                  onChange={(e) => setCheckBox(e.target.checked)}
                  className="w-4 h-4"
                />
                <label htmlFor="returnTrip" className="text-sm font-medium">
                  Round Trip
                </label>
              </div>
              {error && (
                <Alert variant="destructive" className="mt-2">
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div className="space-y-2">
                <label className="text-sm font-medium flex items-center gap-2">
                  <MapPin size={16} />
                  From
                </label>
                <select
                  value={departure}
                  onChange={(e) => setDeparture(e.target.value)}
                  className="w-full p-2 border rounded-md"
                >
                  <option value="">Select Departure</option>
                  {cities.map((city) => (
                    <option key={`dep-${city}`} value={city}>{city}</option>
                  ))}
                </select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium flex items-center gap-2">
                  <MapPin size={16} />
                  To
                </label>
                <select
                  value={destination}
                  onChange={(e) => setDestination(e.target.value)}
                  className="w-full p-2 border rounded-md"
                >
                  <option value="">Select Destination</option>
                  {cities.map((city) => (
                    <option key={`dest-${city}`} value={city}>{city}</option>
                  ))}
                </select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium flex items-center gap-2">
                  <Calendar size={16} />
                  Departure Date
                </label>
                <input
                  type="date"
                  value={departureDate}
                  onChange={(e) => setDepartureDate(e.target.value)}
                  className="w-full p-2 border rounded-md"
                />
              </div>

              {checkBox && (
                <div className="space-y-2">
                  <label className="text-sm font-medium flex items-center gap-2">
                    <RotateCcw size={16} />
                    Return Date
                  </label>
                  <input
                    type="date"
                    value={returnDate}
                    onChange={(e) => setReturnDate(e.target.value)}
                    className="w-full p-2 border rounded-md"
                  />
                </div>
              )}
            </div>

            <div className="mt-6 flex justify-center">
              <button
                onClick={fetchFlights}
                disabled={loading}
                className="bg-blue-600 text-white px-8 py-3 rounded-full hover:bg-blue-700 transition-colors flex items-center gap-2"
              >
                <Search size={20} />
                {loading ? 'Searching...' : 'Search Flights'}
              </button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Flight Results */}
      {flights.length > 0 && (
        <div className="container mx-auto px-4 mb-12">
          <h2 className="text-2xl font-bold mb-6">Available Flights</h2>
          <div className="grid grid-cols-1 gap-4">
            {flights
              .filter(flight => 
                checkBox
                  ? (flight.origin === departure && flight.destination === destination) ||
                    (flight.origin === destination && flight.destination === departure)
                  : flight.origin === departure && flight.destination === destination
              )
              .map((flight) => (
                <Card key={flight._id} className="p-6">
                  <div className="flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
                    <div className="flex items-center gap-4">
                      <Plane className="text-blue-600" size={32} />
                      <div>
                        <h3 className="font-bold text-lg">{flight.flightName}</h3>
                        <p className="text-gray-600">Flight #{flight.flightId}</p>
                      </div>
                    </div>

                    <div className="flex flex-col md:flex-row gap-8 flex-grow justify-around">
                      <div className="text-center">
                        <p className="text-sm text-gray-600">Departure</p>
                        <p className="font-bold">{flight.departureTime}</p>
                        <p className="text-blue-600">{flight.origin}</p>
                      </div>

                      <div className="text-center">
                        <p className="text-sm text-gray-600">Arrival</p>
                        <p className="font-bold">{flight.arrivalTime}</p>
                        <p className="text-blue-600">{flight.destination}</p>
                      </div>

                      <div className="text-center">
                        <p className="text-sm text-gray-600">Price</p>
                        <p className="font-bold text-lg">₹{flight.basePrice}</p>
                        <p className="text-sm text-gray-600">{flight.totalSeats} seats left</p>
                      </div>
                    </div>

                    <button
                      onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}
                      className="bg-green-600 text-white px-6 py-2 rounded-full hover:bg-green-700 transition-colors w-full md:w-auto"
                    >
                      Book Now
                    </button>
                  </div>
                </Card>
              ))}
          </div>
        </div>
      )}

      {/* About Section */}
      <section className="bg-gray-50 py-16">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold mb-8 text-center">About Us</h2>
          <div className="max-w-4xl mx-auto space-y-6 text-gray-600">
            <p>
              Welcome to our Flight ticket booking app, where we are dedicated to providing you with an exceptional travel experience from start to finish. Whether you're embarking on a daily commute, planning an exciting cross-country adventure, or seeking a leisurely scenic route, our app offers an extensive selection of Flight options to cater to your unique travel preferences.
            </p>
            <p>
              We understand the importance of convenience and efficiency in your travel plans. Our user-friendly interface allows you to effortlessly browse through a wide range of Flight schedules, compare fares, and choose the most suitable seating options.
            </p>
            <p>
              With our Flight ticket booking app, you can embrace the joy of exploring new destinations, immerse yourself in breathtaking scenery, and create cherished memories along the way.
            </p>
          </div>
          
          <footer className="mt-12 text-center text-gray-600">
            <p>2024 DFK FlightConnect - © All rights reserved</p>
          </footer>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
