import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [flights, setFlights] = useState([]);
  const [searchPerformed, setSearchPerformed] = useState(false);

  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);

  useEffect(() => {
    if (localStorage.getItem('userType') === 'admin') {
      navigate('/admin');
    } else if (localStorage.getItem('userType') === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, [navigate]);

  const cities = [
    "Chennai", "Bangalore", "Hyderabad", "Mumbai", "Indore",
    "Delhi", "Pune", "Trivandrum", "Bhopal", "Kolkata",
    "Varanasi", "Jaipur"
  ];

  const fetchFlights = async () => {
    if (!validateInputs()) return;
    
    setLoading(true);
    setSearchPerformed(true);
    
    try {
      const response = await axios.get('http://localhost:6001/fetch-flights');
      setFlights(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch flights. Please try again.');
      setFlights([]);
    } finally {
      setLoading(false);
    }
  };

  const validateInputs = () => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    
    const depDate = new Date(departureDate);
    const retDate = checkBox ? new Date(returnDate) : null;

    if (!departure || !destination || !departureDate) {
      setError("Please fill all required fields");
      return false;
    }

    if (departure === destination) {
      setError("Departure and destination cities cannot be the same");
      return false;
    }

    if (depDate < today) {
      setError("Departure date cannot be in the past");
      return false;
    }

    if (checkBox && (!returnDate || retDate <= depDate)) {
      setError("Return date must be after departure date");
      return false;
    }

    return true;
  };

  const handleTicketBooking = (id, origin, destination) => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      navigate('/auth');
      return;
    }

    if (origin === departure) {
      setTicketBookingDate(departureDate);
    } else if (destination === departure) {
      setTicketBookingDate(returnDate);
    }
    navigate(`/book-flight/${id}`);
  };

  const getFilteredFlights = () => {
    if (!checkBox) {
      return flights.filter(flight => 
        flight.origin.toLowerCase() === departure.toLowerCase() && 
        flight.destination.toLowerCase() === destination.toLowerCase()
      );
    }
    
    return flights.filter(flight => 
      (flight.origin.toLowerCase() === departure.toLowerCase() && 
       flight.destination.toLowerCase() === destination.toLowerCase()) ||
      (flight.origin.toLowerCase() === destination.toLowerCase() && 
       flight.destination.toLowerCase() === departure.toLowerCase())
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div style={{ background: 'linear-gradient(to right, #1a365d, #2563eb)' }} className="text-white py-20 px-4">
        <div className="max-w-4xl mx-auto text-center">
          <h1 className="text-4xl md:text-5xl font-bold mb-6">
            Embark on an Extraordinary Flight Booking Adventure! ✈️
          </h1>
          <p className="text-xl md:text-2xl opacity-90">
            Unleash your travel desires and book extraordinary flight journeys that will transport you to unforgettable destinations.
          </p>
        </div>
      </div>

      {/* Search Container */}
      <div className="max-w-4xl mx-auto -mt-10 px-4">
        <div className="bg-white rounded-lg shadow-xl p-6">
          <div className="flex items-center gap-2 mb-6">
            <input
              type="checkbox"
              id="returnTrip"
              className="w-4 h-4"
              checked={checkBox}
              onChange={(e) => setCheckBox(e.target.checked)}
            />
            <label htmlFor="returnTrip" className="text-gray-700">Return Journey</label>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div className="space-y-2">
              <label className="text-sm font-medium text-gray-700">Departure City</label>
              <select
                className="w-full p-2 border rounded-md"
                value={departure}
                onChange={(e) => setDeparture(e.target.value)}
              >
                <option value="">Select City</option>
                {cities.map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium text-gray-700">Destination City</label>
              <select
                className="w-full p-2 border rounded-md"
                value={destination}
                onChange={(e) => setDestination(e.target.value)}
              >
                <option value="">Select City</option>
                {cities.map(city => (
                  <option key={city} value={city}>{city}</option>
                ))}
              </select>
            </div>

            <div className="space-y-2">
              <label className="text-sm font-medium text-gray-700">Departure Date</label>
              <input
                type="date"
                className="w-full p-2 border rounded-md"
                value={departureDate}
                onChange={(e) => setDepartureDate(e.target.value)}
              />
            </div>

            {checkBox && (
              <div className="space-y-2">
                <label className="text-sm font-medium text-gray-700">Return Date</label>
                <input
                  type="date"
                  className="w-full p-2 border rounded-md"
                  value={returnDate}
                  onChange={(e) => setReturnDate(e.target.value)}
                />
              </div>
            )}
          </div>

          {error && (
            <div className="mt-4 p-3 bg-red-100 text-red-700 rounded-md">
              {error}
            </div>
          )}

          <button
            className="mt-6 w-full bg-blue-600 text-white py-3 rounded-md hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
            onClick={fetchFlights}
            disabled={loading}
          >
            {loading ? (
              <span>Searching... 🔄</span>
            ) : (
              <span>Search Flights ✈️</span>
            )}
          </button>
        </div>
      </div>

      {/* Search Results */}
      {searchPerformed && !loading && (
        <div className="max-w-4xl mx-auto mt-8 px-4 mb-12">
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-bold mb-6">
              {getFilteredFlights().length > 0 ? 'Available Flights ✈️' : 'No Flights Found 🚫'}
            </h2>

            <div className="space-y-4">
              {getFilteredFlights().map((flight) => (
                <div
                  key={flight._id}
                  className="border rounded-lg p-4 hover:shadow-md transition-shadow"
                >
                  <div className="grid md:grid-cols-4 gap-4">
                    <div>
                      <h3 className="font-bold text-lg">{flight.flightName}</h3>
                      <p className="text-gray-600">Flight: {flight.flightId}</p>
                    </div>
                    
                    <div>
                      <p className="font-medium">{flight.origin}</p>
                      <p className="text-gray-600">{flight.departureTime}</p>
                    </div>
                    
                    <div>
                      <p className="font-medium">{flight.destination}</p>
                      <p className="text-gray-600">{flight.arrivalTime}</p>
                    </div>
                    
                    <div className="flex flex-col md:items-end">
                      <p className="font-bold text-lg">₹{flight.basePrice}</p>
                      <button
                        className="mt-2 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors"
                        onClick={() => handleTicketBooking(flight._id, flight.origin, flight.destination)}
                      >
                        Book Now 🎫
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* About Section */}
      <section id="about" className="bg-gray-100 py-16 px-4">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-3xl font-bold mb-8 text-center">About Us ✨</h2>
          <div className="space-y-6 text-gray-700">
            <p className="leading-relaxed">
              Welcome to our Flight ticket booking app, where we are dedicated to providing you with an exceptional travel experience from start to finish. Whether you're embarking on a daily commute, planning an exciting cross-country adventure, or seeking a leisurely scenic route, our app offers an extensive selection of Flight options to cater to your unique travel preferences.
            </p>
            <p className="leading-relaxed">
              We understand the importance of convenience and efficiency in your travel plans. Our user-friendly interface allows you to effortlessly browse through a wide range of Flight schedules, compare fares, and choose the most suitable seating options. With just a few taps, you can secure your Flight tickets and be one step closer to your desired destination.
            </p>
            <p className="leading-relaxed">
              With our Flight ticket booking app, you can embrace the joy of exploring new destinations, immerse yourself in breathtaking scenery, and create cherished memories along the way. Start your journey today and let us be your trusted companion in making your Flight travel dreams a reality.
            </p>
          </div>
          <div className="mt-8 text-center text-gray-600">
            <p>2023 DFK FlightConnect - &copy; All rights reserved</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default LandingPage;
