import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GeneralContext } from '../context/GeneralContext';
import '../styles/LandingPage.css';

const LandingPage = () => {
  const [error, setError] = useState('');
  const [checkBox, setCheckBox] = useState(false);
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');
  const [returnDate, setReturnDate] = useState('');
  const [flights, setFlights] = useState([]);
  const navigate = useNavigate();
  const { setTicketBookingDate } = useContext(GeneralContext);

  useEffect(() => {
    const userType = localStorage.getItem('userType');
    if (userType === 'admin') {
      navigate('/admin');
    } else if (userType === 'flight-operator') {
      navigate('/flight-admin');
    }
  }, [navigate]);

  const fetchFlights = async () => {
    setError('');

    if (!departure || !destination || !departureDate || (checkBox && !returnDate)) {
      setError('Please fill all required fields.');
      return;
    }

    const date = new Date();
    const depDate = new Date(departureDate);
    const retDate = new Date(returnDate);

    if (depDate < date || (checkBox && retDate < depDate)) {
      setError('Please provide valid dates.');
      return;
    }

    try {
      const response = await axios.get('http://localhost:6001/fetch-flights');
      setFlights(response.data);
    } catch (error) {
      setError('Failed to fetch flights. Please try again later.');
    }
  };

  const handleTicketBooking = (id, origin, destination) => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      navigate('/auth');
      return;
    }

    const selectedDate = origin === departure ? departureDate : returnDate;
    setTicketBookingDate(selectedDate);
    navigate(`/book-flight/${id}`);
  };

  return (
    <div className="landing-page">
      <header className="landing-header">
        <h1>Embark on an Extraordinary Flight Booking Adventure!</h1>
        <p>
          Discover unforgettable destinations and enjoy seamless travel booking
          with our platform.
        </p>
      </header>

      <div className="flight-search-container">
        <div className="search-form">
          <div className="form-switch">
            <label>
              <input
                type="checkbox"
                checked={checkBox}
                onChange={(e) => setCheckBox(e.target.checked)}
              />
              Return Journey
            </label>
          </div>

          <div className="form-fields">
            <select
              className="form-select"
              value={departure}
              onChange={(e) => setDeparture(e.target.value)}
            >
              <option value="" disabled>
                Select Departure City
              </option>
              {['Chennai', 'Bangalore', 'Hyderabad', 'Mumbai', 'Delhi'].map(
                (city) => (
                  <option key={city} value={city}>
                    {city}
                  </option>
                )
              )}
            </select>

            <select
              className="form-select"
              value={destination}
              onChange={(e) => setDestination(e.target.value)}
            >
              <option value="" disabled>
                Select Destination City
              </option>
              {['Chennai', 'Bangalore', 'Hyderabad', 'Mumbai', 'Delhi'].map(
                (city) => (
                  <option key={city} value={city}>
                    {city}
                  </option>
                )
              )}
            </select>

            <input
              type="date"
              className="form-date"
              value={departureDate}
              onChange={(e) => setDepartureDate(e.target.value)}
            />

            {checkBox && (
              <input
                type="date"
                className="form-date"
                value={returnDate}
                onChange={(e) => setReturnDate(e.target.value)}
              />
            )}

            <button className="btn search-btn" onClick={fetchFlights}>
              Search Flights
            </button>
          </div>

          {error && <p className="error-text">{error}</p>}
        </div>
      </div>

      {flights.length > 0 ? (
        <div className="flights-container">
          <h2>Available Flights</h2>
          <div className="flights-list">
            {flights
              .filter(
                (flight) =>
                  flight.origin === departure &&
                  flight.destination === destination
              )
              .map((flight) => (
                <div className="flight-card" key={flight._id}>
                  <h3>{flight.flightName}</h3>
                  <p>Flight Number: {flight.flightId}</p>
                  <p>Departure: {flight.departureTime}</p>
                  <p>Arrival: {flight.arrivalTime}</p>
                  <p>Price: â‚¹{flight.basePrice}</p>
                  <button
                    className="btn book-btn"
                    onClick={() =>
                      handleTicketBooking(flight._id, flight.origin, flight.destination)
                    }
                  >
                    Book Now
                  </button>
                </div>
              ))}
          </div>
        </div>
      ) : (
        <div className="no-flights">No Flights Available</div>
      )}
    </div>
  );
};

export default LandingPage;
